/**
 * - Store Postman BDD functions in helperFunctions
 * 
 **/

function expectedReturn(status, entryName) {
    it('expect to return ' + entryName, function() {
        expect(response).to.have.status(status);
        expect(response).to.be.json;
        if(entryName != undefined)
          expect(response.body).to.not.be.empty;
    });
}

function expectedHeader(entryName) {
    describe('header', function() {
        it('expect to have content type', function(){
            expect(response).to.have.header('Content-Type');
        });
        /*if(entryName != undefined)
          it('expect to have content length', function(){
              expect(response).to.have.header('Content-Length');
          });*/
    });
}

function expectedSchema(entryName, body) {
  var schema = JSON.parse(globals["alfresco-schema"]);
  tv4.addSchema('http://alfresco.com/schema', schema);
  var fragment = '#/definitions/' + entryName;
  var subschema = tv4.getSchema('http://alfresco.com/schema' + fragment);

  it('expect to have schema ' + fragment, function() {
    expect(body).to.have.schema(subschema);
  });
}

function expectedRequestBody(entry) {
    if(entry != undefined)
        describe('RequestBody', function() {
            expectedSchema(entry, request.body);
    });
}

function expectedResponseBody(entry, otherResponseBodyChecks) {
    if(entry != undefined)
        describe('ResponseBody', function() {
            expectedSchema(entry, response.body);
            if(otherResponseBodyChecks !== undefined)
                otherResponseBodyChecks();
    });
}

function describeResponse(status, entryNameResponse, otherResponseBodyChecks,  entryNameRequest){
    if(globals['debugModeOn'] === 'true' || globals['debugModeOn'] === undefined)
        describe('"' + request.name + '"', function(){
            expectedReturn(status, entryNameResponse);
            expectedHeader(entryNameResponse);
            expectedRequestBody(entryNameRequest);
            expectedResponseBody(entryNameResponse, otherResponseBodyChecks);
        });
}

postman.setGlobalVariable('helperFunctions', describeResponse.toString() + expectedReturn.toString() + expectedHeader.toString() + expectedRequestBody.toString() + expectedResponseBody.toString() + expectedSchema.toString() + responseBody);

